mapper.select:
        SELECT COUNT(1)
		  FROM MEMBERS
		 WHERE EMAIL = #{_parameter}

        SELECT SALT
		  FROM MEMBERS
		 WHERE EMAIL = #{_parameter}

mapper.update:
        UPDATE MEMBERS
		   SET LOGIN_CNT = 0
		     , LATEST_LOGIN_SUCCESS_DATE = SYSDATE
		     , LATEST_ACCESS_IP = #{latestAccessIp}
		 WHERE EMAIL = #{email}

        UPDATE MEMBERS
		   SET LOGIN_CNT = LOGIN_CNT + 1
		     , LATEST_LOGIN_FAIL_DATE = SYSDATE
		     , LATEST_ACCESS_IP = #{latestAccessIp}
		 WHERE EMAIL = #{email}
